          <TabsContent value="memories" className="mt-6">
            {renderMemoryGrid()}
          </TabsContent>

          <TabsContent value="adventures" className="mt-6">
            {renderPlansSection()}
          </TabsContent>
        </Tabs>
      )}
    </div>
  )
}

"use client"

import React from "react"
import Image from "next/image"
