    )
  }
}

import { NextRequest, NextResponse } from "next/server"
import { adminAuth, adminDb, FieldValue } from "@/lib/firebase-admin"

export async function POST(req: NextRequest, { params }: { params: { planId: string } }) {
  try {
    const authHeader = req.headers.get("authorization") || ""
    const token = authHeader.startsWith("Bearer ") ? authHeader.slice(7) : undefined
    if (!token) return NextResponse.json({ error: "Missing Authorization header" }, { status: 401 })

    const decoded = await adminAuth.verifyIdToken(token)
    const userId = decoded.uid

    const planId = params?.planId
    if (!planId) return NextResponse.json({ error: "planId is required" }, { status: 400 })

    const planRef = adminDb.collection("plans").doc(planId)
    const userRef = adminDb.collection("users").doc(userId)

    const result = await adminDb.runTransaction(async (tx: any) => {
      const [planSnap, userSnap] = await Promise.all([tx.get(planRef), tx.get(userRef)])
      if (!planSnap.exists) throw new Error("Plan not found")
      if (!userSnap.exists) throw new Error("User not found")

      const planData = planSnap.data() as any
      const userData = userSnap.data() as any

      const participantIds: string[] = Array.isArray(planData.participantIds) ? planData.participantIds : []
      const currentParticipants: number = Number(planData.currentParticipants || participantIds.length || 0)
      const maxParticipants: number | null = planData.maxParticipants ?? null

      const alreadyJoined = participantIds.includes(userId)
      if (alreadyJoined) {
        return { joined: true, pending: false }
      }

      // For private plans, add a pending request instead of immediate join
      if (planData.isPrivate) {
        const pendingRequests: string[] = Array.isArray(planData.pendingJoinRequests)
          ? planData.pendingJoinRequests
          : []
        if (!pendingRequests.includes(userId)) {
          tx.update(planRef, {
            pendingJoinRequests: FieldValue.arrayUnion(userId),
          })
          // Notify organizer of join request
          const notifRef = adminDb.collection("notifications").doc()
          tx.set(notifRef, {
            id: notifRef.id,
            toUserId: planData.userId,
            fromUser: {
              id: userId,
              username: userData.username,
              name: userData.displayName || userData.username,
              avatar: userData.photoURL,
            },
            type: "plan_join_request",
            planId,
            content: `${userData.displayName || "Someone"} requested to join your plan "${planData.title}"`,
            isRead: false,
            createdAt: new Date(),
          })
        }
        return { joined: false, pending: true }
      }

      // Public plan - enforce capacity
      if (maxParticipants && currentParticipants >= maxParticipants) {
        throw new Error("Plan is full")
      }

      tx.update(planRef, {
        participantIds: FieldValue.arrayUnion(userId),
        currentParticipants: (currentParticipants || 0) + 1,
        updatedAt: new Date(),
      })

      // Notify organizer of new participant
      if (planData.userId !== userId) {
        const notifRef = adminDb.collection("notifications").doc()
        tx.set(notifRef, {
          id: notifRef.id,
          toUserId: planData.userId,
          fromUser: {
            id: userId,
            username: userData.username,
            name: userData.displayName || userData.username,
            avatar: userData.photoURL,
          },
          type: "plan_join",
          planId,
          content: `${userData.displayName || "Someone"} joined your plan "${planData.title}"`,
          isRead: false,
          createdAt: new Date(),
        })
      }

      return { joined: true, pending: false }
    })

    return NextResponse.json({ ok: true, ...result }, { status: 200 })
  } catch (err: any) {
    const message = String(err?.message || err)
    const status = /not found/i.test(message) ? 404 : /full|capacity/i.test(message) ? 409 : 500
    console.error("[API] /api/plans/[planId]/join POST error", err)
    return NextResponse.json({ error: message || "Server error" }, { status })
  }
}

import { NextRequest, NextResponse } from "next/server"
import { adminAuth, adminDb } from "@/lib/firebase-admin"

export async function POST(req: NextRequest) {
  try {
    const authHeader = req.headers.get("authorization") || ""
    const token = authHeader.startsWith("Bearer ") ? authHeader.slice(7) : undefined
    if (!token) return NextResponse.json({ error: "Missing Authorization header" }, { status: 401 })

    const decoded = await adminAuth.verifyIdToken(token)
    const userId = decoded.uid

    const body = await req.json()
    const {
      title,
      description,
      imageUrl,
      location,
      dateTime,
      maxParticipants,
      costPerHead,
      currency,
      isPrivate = false,
    } = body || {}

    // Validation
    if (!title || typeof title !== "string" || title.trim().length < 3 || title.length > 120) {
      return NextResponse.json({ error: "Invalid title (3-120 chars)" }, { status: 400 })
    }
    if (!location || typeof location !== "string" || location.trim().length < 2) {
      return NextResponse.json({ error: "Invalid location" }, { status: 400 })
    }
    if (!dateTime) {
      return NextResponse.json({ error: "dateTime is required" }, { status: 400 })
    }
    const dt = new Date(dateTime)
    if (isNaN(dt.getTime()) || dt.getTime() < Date.now() - 60_000) {
      return NextResponse.json({ error: "dateTime must be a future date" }, { status: 400 })
    }
    if (!currency || typeof currency !== "string") {
      return NextResponse.json({ error: "currency is required" }, { status: 400 })
    }
    if (costPerHead != null && (typeof costPerHead !== "number" || costPerHead < 0)) {
      return NextResponse.json({ error: "costPerHead must be a non-negative number" }, { status: 400 })
    }
    if (maxParticipants != null) {
      if (typeof maxParticipants !== "number" || maxParticipants < 1 || maxParticipants > 1000) {
        return NextResponse.json({ error: "maxParticipants must be between 1 and 1000" }, { status: 400 })
      }
    }

    const userSnap = await adminDb.collection("users").doc(userId).get()
    if (!userSnap.exists) {
      return NextResponse.json({ error: "Organizer user not found" }, { status: 404 })
    }
    const userData = userSnap.data() || {}

    const planRef = adminDb.collection("plans").doc()
    const payload = {
      id: planRef.id,
      userId,
      organizer: {
        id: userId,
        username: userData.username || "unknown",
        name: userData.displayName || userData.username || "Unknown User",
        avatar: userData.photoURL || "/placeholder.svg",
      },
      title: title.trim(),
      description: (description || "").toString(),
      imageUrl: imageUrl || "",
      location: location.trim(),
      dateTime: dt,
      maxParticipants: maxParticipants ?? null,
      participantIds: [userId], // Organizer is first participant
      currentParticipants: 1,
      costPerHead: Number(costPerHead ?? 0),
      currency,
      isPrivate: !!isPrivate,
      createdAt: new Date(),
      updatedAt: new Date(),
    }

    const batch = adminDb.batch()
    batch.set(planRef, payload)
    // optional: increment organizer's plans count if tracked
    const userRef = adminDb.collection("users").doc(userId)
    batch.update(userRef, { plans: (userData.plans || 0) + 1 })
    await batch.commit()
    return NextResponse.json({ id: planRef.id }, { status: 201 })
  } catch (err: any) {
    console.error("[API] /api/plans POST error", err)
    return NextResponse.json({ error: "Server error" }, { status: 500 })
  }
}

