  ENABLE_PWA: toBool(getEnv("NEXT_PUBLIC_ENABLE_PWA"), true),
  ENABLE_ANALYTICS: toBool(getEnv("NEXT_PUBLIC_ENABLE_ANALYTICS"), true),
